import React, { useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import { GetProductList } from '../APIRequest/APIRequest';
import { useSelector, useDispatch } from 'react-redux';
import { fetchProducts } from '../redux/actions'; // Assuming you have an action to fetch products

const ProductList = () => {
    const dispatch = useDispatch();
    const ALLProduct = useSelector((state) => state.product.allProducts); // Assuming you have a slice named 'product' with 'allProducts'
    
    useEffect(() => {
        dispatch(fetchProducts()); // Dispatch an action to fetch products when the component mounts
    }, [dispatch]);

    return (
        <div>
            <h2>My Product List</h2>
            <br />
            <br />
            <Table striped>
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Price</th>
                        <th>Stock</th>
                        <th>Code</th>
                    </tr>
                </thead>
                <tbody>
                    {ALLProduct.map((item, i) => (
                        <tr key={item.id}> {/* Assuming each product has a unique 'id' */}
                            <td>
                                <div className='d-flex px-2 py-1'>
                                    <div>
                                        <img src={item.image} className='avatar me-3' alt='' />
                                    </div>
                                    <div className='d-flex flex-column justify-content-center'>
                                        <h6 className='mb-0 text-xs'>{item.title}</h6>
                                        <p className='text-xs text-secondary mb-0'>{item.category}</p>
                                    </div>
                                </div>
                            </td>
                            <td>
                                <p className='text-xs font-weight-bold mb-0'>{item.brand}</p>
                                <p className='text-xs text-secondary mb-0'>{item.price} Taka</p>
                            </td>
                            <td>
                                <p className='badge bg-gradient-success'>{item.stock}</p>
                            </td>
                            <td>
                                <span className='text-secondary text-xs font-weight-bold'>{item.product_code}</span>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    );
};

export default ProductList;
